rwble_hl_reset_handler _rwble_hl_reset_handler = _rwble_hl_reset;

/*
 * EXPORTED FUNCTIONS DEFINITIONS
 ****************************************************************************************
 */

#if(!BLE_EMB_PRESENT)
    // needed for encryption if controller not present
    extern void gapm_smp_send_hci_encrypt(const uint8_t *key, const uint8_t *val);
#endif //(!BLE_EMB_PRESENT)



#ifdef CC2564C_CONTROLLER
void rwble_init_ti_cc2564c(void)
{
    uint32_t i;
    uint32_t len;

    len = sizeof(g_ti_cc2564c_init_script) / sizeof(g_ti_cc2564c_init_script[0]);

    for (i = 0; i < len; i++)
    {
        h4tl_write(HCI_CMD_MSG_TYPE, &g_ti_cc2564c_init_script[i][1],
                   g_ti_cc2564c_init_script[i][3] + 4 - 1, NULL);

        rw_os_delay_ms(100);
    }

}
#endif /*CC2564C_CONTROLLER*/

void _rwble_hl_init(void)
{
#if (BLE_CENTRAL || BLE_PERIPHERAL)
#if (BLE_ATTS)
    /* Initialize database */
    attm_init(false);
#endif /* (BLE_ATTS) */
#endif /* #if (BLE_CENTRAL || BLE_PERIPHERAL) */

    // Initialize GAP
    gapm_init(false);

#if (BLE_CENTRAL || BLE_PERIPHERAL)
    // Initialize GATT
    gattm_init(false);
    // Initialize L2CAP
    l2cm_init(false);
#endif /* #if (BLE_CENTRAL || BLE_PERIPHERAL) */

#if (BLE_ISO_MODE_0_PROTOCOL)
    // Initialize Audio Mode 0
    am0_task_init(false);
#endif // (BLE_ISO_MODE_0_PROTOCOL)

#if (BLE_RSA)
    rsa_init(false);
#endif //(BLE_RSA)

#ifdef CC2564C_CONTROLLER

    rwble_init_ti_cc2564c();
#endif
}


/**
 ****************************************************************************************
 * @brief Initialize the host (reset requested)
 *
 ****************************************************************************************
 */
void _rwble_hl_reset(void)
{
#if (BLE_CENTRAL || BLE_PERIPHERAL)
#if (BLE_ATTS)
    /* Initialize database */
    attm_init(true);
#endif /* (BLE_ATTS) */
#endif /* #if (BLE_CENTRAL || BLE_PERIPHERAL) */

#if (BLE_ISO_MODE_0_PROTOCOL)
    // Initialize Audio Mode 0
    am0_task_init(true);
#endif // (BLE_ISO_MODE_0_PROTOCOL)

    // initialize GAP
    gapm_init(true);

#if (BLE_CENTRAL || BLE_PERIPHERAL)
    // Initialize GATT & ATT
    gattm_init(true);
    // Initialize L2CAP
    l2cm_init(true);
#endif /* #if (BLE_CENTRAL || BLE_PERIPHERAL) */

#if (BLE_RSA)
    rsa_init(true);
#endif //(BLE_RSA)

#if (!BLE_EMB_PRESENT)
    rwip_reset();
#endif // (!BLE_EMB_PRESENT)
}

#if(!BLE_EMB_PRESENT)
/**
 ****************************************************************************************
 * @brief Start AES encryption
 *
 * The exchange memory must be filled before calling this function.
 * This function expect to be called from a BLE Module
 *
 * @param[in] key           AES Encryption key must be 16 bytes
 * @param[in] val           16 bytes value array to encrypt using AES
 ****************************************************************************************
 */
void rwip_aes(const uint8_t *key, const uint8_t *val, bool cipher)
{
    // Ask SMP Manager to request encryption over HCI
    gapm_smp_send_hci_encrypt(key, val);
}
#endif // (!BLE_EMB_PRESENT)

